    /*
     * Listing of profesores
     */
    function index()
    {
        $params['limit'] = RECORDS_PER_PAGE; 
        $params['offset'] = ($this->input->get('per_page')) ? $this->input->get('per_page') : 0;
        
        $config = $this->config->item('pagination');
        $config['base_url'] = site_url('profesor/index?');
        $config['total_rows'] = $this->Profesores_model->get_all_profesores_count();
        $this->pagination->initialize($config);

        $data['profesores'] = $this->Profesores_model->get_all_profesores($params);
        
        $data['_view'] = 'profesor/index';
        $this->load->view('layouts/main',$data);
    }

    /*
     * Adding a new profesor
     */
    function add()
    {   
        $this->load->library('form_validation');

		$this->form_validation->set_rules('profesor_estado','Profesor Estado','required');
