    /*
     * Deleting profesor
     */
    function remove($id_profesores)
    {
        $profesor = $this->Profesores_model->get_profesor($id_profesores);

        // check if the profesor exists before trying to delete it
        if(isset($profesor['id_profesores']))
        {
            $this->Profesores_model->delete_profesor($id_profesores);
            redirect('profesores/index');
        }
        else
            show_error('The profesor you are trying to delete does not exist.');
    }
    
